import{_ as r,o as a,c as e,e as s}from"./app.04f7da4a.js";const c="/yuanse-project/assets/wuyesensei.54b6598c.png",h={},d=s('<h1 id="\u4F59\u5F26\u5267\u573A" tabindex="-1"><a class="header-anchor" href="#\u4F59\u5F26\u5267\u573A" aria-hidden="true">#</a> \u4F59\u5F26\u5267\u573A</h1><p>\u7F18\u83BA\u5B66\u56EDcos\u90E8\u7684\u76F4\u5C5E\u539F\u533A\uFF0C\u8FD9\u91CC\u6709\u7740\u5168\u7F18\u83BA\u5C9B\u6700\u4E30\u5BCC\u7684\u5F02\u80FD\uFF0C\u56E0\u4E3A\u6210\u5458\u5F88\u591A\u90FD\u662F\u5F02\u4E16\u754C\u7A7F\u8D8A\u8005\uFF0C\u4F8B\u5982\u9B54\u6CD5\u5E08\u3001\u82F1\u7075\u3001\u804C\u4E1A\u82F1\u96C4\u7B49\u7B49\u3002\u4ED6\u4EEC\u53EF\u4EE5\u5728\u539F\u533A\u5185\u4F7F\u7528\u81EA\u5DF1\u7684\u80FD\u529B\uFF0C\u4F46\u662F\u4E0D\u4F1A\u9020\u6210\u4EFB\u4F55\u4F24\u5BB3\uFF0C\u56E0\u4E3A\u8FD9\u4E2A\u539F\u533A\u7684\u4EBA\u4EEC\u90FD\u4ECE\u4E8B\u7740cos\u548Ccos\u821E\u53F0\u5267\u7684\u5DE5\u4F5C\u3002\u539F\u533A\u5185\u7684\u666F\u8272\u90FD\u662F\u53EF\u4EE5\u4EBA\u4E3A\u6539\u53D8\u7684\uFF0C\u56E0\u4E3A\u6BCF\u4E2A\u5730\u65B9\u90FD\u6709\u53EF\u80FD\u6210\u4E3A\u821E\u53F0\u5267\u7684\u821E\u53F0\uFF0C\u6240\u4EE5\u8FD9\u91CC\u4F1A\u6709\u903C\u771F\u7684\u6218\u6597\u3001\u8D34\u8FD1ACG\u4F5C\u54C1\u7684\u6F14\u51FA\u7B49\u7B49\uFF0C\u6240\u4EE5\u8FD9\u91CC\u66F4\u5E94\u8BE5\u8BF4\u662F\u4E00\u4E2A\u5267\u573A\u3002\u7531\u4E8Ecos\u672C\u8EAB\u662F\u6570\u5B66\u4E0A\u7684\u4F59\u5F26\u7B26\u53F7\uFF0C\u6240\u4EE5\u53EB\u201C\u4F59\u5F26\u5267\u573A\u201D\u3002</p><h2 id="\u89D2\u8272" tabindex="-1"><a class="header-anchor" href="#\u89D2\u8272" aria-hidden="true">#</a> \u89D2\u8272</h2><h3 id="\u96FE\u591C\u8001\u5E08" tabindex="-1"><a class="header-anchor" href="#\u96FE\u591C\u8001\u5E08" aria-hidden="true">#</a> \u96FE\u591C\u8001\u5E08</h3><p><img src="'+c+'" alt=""><br> \u4F59\u5F26\u5267\u56E2\u7684\u56E2\u957F\uFF0C\u672C\u540D\u4E3A\u96FE\u5C9B\u591C\u4E43\u3002<br> \u62E5\u6709\u5982\u767D\u96EA\u4E00\u822C\u7684\u957F\u53D1\uFF0C\u672C\u4EBA\u620F\u79F0\u662F\u56E0\u4E3A\u81EA\u5DF1\u662F\u4E0A\u5929\u9009\u4E2D\u7684\u4E16\u754C\u4E0A\u6700\u7EAF\u6D01\u5584\u826F\u7684\u4EBA\u624D\u4F1A\u6709\u767D\u96EA\u516C\u4E3B\u4E00\u6837\u7684\u767D\u53D1\uFF0C\u4F46\u5B9E\u9645\u4E0A\u6027\u683C\u8179\u9ED1\uFF0C\u5728\u663E\u9732\u672C\u6027\u7684\u65F6\u5019\u77B3\u8272\u4F1A\u53D8\u6210\u8840\u7EA2\u8272\uFF0C\u4E0D\u8FC7\u8FD9\u662F\u773C\u775B\u672C\u6765\u7684\u7684\u989C\u8272\uFF0C\u4E00\u5F00\u59CB\u7684\u84DD\u77B3\u53EA\u662F\u7F8E\u77B3\u3002<br> \u5174\u8DA3\u662F\u62C9\u62E2\u5404\u79CD\u5947\u602A\u7684\u56E2\u5458\u8FDB\u5165cos\u90E8\u7684\u5267\u56E2\uFF0C\u5E76\u4EE5\u5404\u79CD\u65B9\u5F0F\u5F15\u8BF1\uFF0C\u663E\u9732\u672C\u6027\u7684\u65F6\u5019\u7ED9\u4EBA\u4E00\u79CD\u8840\u4E4B\u5973\u738B\u4E00\u822C\u7684\u611F\u89C9\u3002\u975E\u5E38\u4E0D\u80FD\u5BB9\u5FCD\u6709\u56E2\u5458\u5728\u6392\u7EC3\u5F00\u59CB\u7684\u65F6\u5019\u5495\u5495\uFF0C\u58F0\u79F0\u67091566\u79CD\u65B9\u5F0F\u8BA9\u4ED6\u4EEC\u7684\u8EAB\u4F53\u597D\u597D\u8BB0\u4F4F\u8FDF\u5230\u7684\u540E\u679C\u662F\u4EC0\u4E48\u6837\u7684\u3002\u4E0D\u8FC7\uFF0C\u6392\u7EC3\u7684\u65F6\u5019\u5374\u610F\u5916\u5730\u8010\u5FC3\uFF0C\u4E5F\u4E0D\u662F\u5F88\u591A\u4EBA\u4EE5\u4E3A\u7684\u4F1A\u968F\u4FBF\u53D1\u706B\u7684\u7C7B\u578B\uFF0C\u5B9E\u9645\u4E0A\u4E5F\u5F88\u597D\u8BF4\u8BDD\u3002 \u975E\u5E38\u559C\u6B22\u6349\u5F04\u963F\u52C7\u548C\u963F\u6597\uFF0C\u603B\u662F\u4F1A\u7528\u5404\u79CD\u7406\u7531\u53BB\u6311\u62E8\u4E24\u4E2A\u4EBA\u7684\u6218\u6597\uFF0C\u7136\u540E\u81EA\u5DF1\u6D25\u6D25\u6709\u5473\u5730\u5403\u7740\u85AF\u7247\u559D\u7740\u53EF\u4E50\u770B\u620F\u3002<br> \u7531\u4E8E\u5728\u6392\u7EC3\u4E2D\u770B\u5230\u963F\u52C7\u4ED6\u4EEC\u5BF9\u201C\u600E\u4E48\u6454\u4E5F\u4E0D\u4F1A\u53D7\u4F24\u7684\u5E8A\u57AB\u201D\u50CF\u5E7C\u7A1A\u56ED\u7684\u5C0F\u5B69\u5B50\u4E00\u6837\u6EDA\u6765\u6EDA\u53BB\uFF0C\u4E00\u77AC\u95F4\u89C9\u5F97\u5F88\u6CBB\u6108\u5E76\u610F\u5916\u6E29\u67D4\u5730\u8BF4\u4E86\u4E00\u53E5\u201C\u597D\u4E86\uFF0C\u6253\u95F9\u4E5F\u8981\u9002\u53EF\u800C\u6B62\u54E6\u201D\uFF0C\u88AB\u60CA\u8BB6\u7684\u4F17\u4EBA\u4E0D\u7981\u611F\u53F9\u4E86\u4E00\u53E5\u201C\u96FE\u591C\u8001\u5E08\u201D\uFF0C\u4E8E\u662F\u4E4B\u540E\u4E5F\u88AB\u56E2\u5458\u4EEC\u8FD9\u4E48\u79F0\u547C\u3002<br> \u6709\u65F6\u5019\u4F1A\u51FA\u6709\u5173\u5973\u738B\u89D2\u8272\u7684cos\uFF0C\u4E0D\u8FC7\u5F53cos\u7F8E\u5C11\u5E74\u7684\u65F6\u5019\u6709\u7740\u5F3A\u5927\u7684\u6740\u4F24\u529B\uFF0C\u4E00\u5EA6\u8BA9\u963F\u52C7\u548C\u963F\u6597\u90FD\u7518\u62DC\u4E0B\u98CE\u3002<br> \u672C\u4EBA\u8FC7\u53BB\u4E3A\u53E4\u8001\u7684\u4E1C\u65B9\u5438\u8840\u4E00\u65CF\u3002\u7531\u4E8E\u65CF\u4EBA\u7684\u547D\u8FD0\u53D7\u5230\u8BC5\u5492\uFF0C\u5982\u63D0\u7EBF\u6728\u5076\u4E00\u6837\u6309\u7167\u65E2\u5B9A\u7684\u5267\u672C\u8FCE\u6765\u7EC8\u5C06\u6BC1\u706D\u7684\u60B2\u5267\uFF0C\u5BFC\u81F4\u6700\u540E\u5168\u65CF\u53EA\u5269\u4E0B\u5979\u4E00\u4E2A\u4EBA\u3002\u56E0\u4E3A\u67D0\u4E9B\u4E0D\u660E\u539F\u56E0\u5979\u9003\u8131\u4E86\u547D\u8FD0\u7684\u675F\u7F1A\uFF0C\u901A\u8FC7\u7F18\u83BA\u4F20\u8BF4\u6765\u5230\u539F\u8272\u4E16\u754C\u3002\u5728\u4E86\u89E3\u5230\u4E86\u821E\u53F0\u5267\u7684\u9B45\u529B\u540E\uFF0C\u5F00\u59CB\u559C\u6B22\u4E0A\u4E86ACG\u7684\u6587\u5316\uFF0C\u4E3A\u4E86\u521B\u9020\u4E00\u4E2A\u201C\u5373\u4F7F\u8131\u79BB\u5267\u672C\u4E5F\u80FD\u8BA9\u6240\u6709\u4EBA\u6709\u7CBE\u5F69\u7684\u6F14\u51FA\u548C\u5B8C\u7F8E\u7684\u7ED3\u5C40\u201D\u7684\u821E\u53F0\u5267\u800C\u6210\u7ACB\u4F59\u5F26\u5267\u56E2\u3002</p><h3 id="\u963F\u52C7" tabindex="-1"><a class="header-anchor" href="#\u963F\u52C7" aria-hidden="true">#</a> \u963F\u52C7</h3><p>\u4F59\u5F26\u5267\u56E2\u7684\u56E2\u5458\uFF0C\u6253\u620F\u7684\u5E38\u4EFB\u62C5\u5F53\u8005\u3002<br> \u5E73\u65F6\u7684\u670D\u9970\u662F\u4E2D\u4E16\u7EAA\u5E02\u6C11\u9636\u5C42\u7684\u670D\u88C5\uFF0C\u8170\u95F4\u522B\u7740\u4E00\u628A\u9053\u5177\u5251\uFF0C\u770B\u8D77\u6765\u50CF\u662F\u91D1\u53D1\u7684\u8D35\u65CF\u7EC5\u58EB\uFF0C\u751A\u81F3\u53EF\u4EE5\u8BF4\u5982\u738B\u5B50\u4E00\u822C\u3002\u4F46\u662F\u672C\u4EBA\u5374\u5F88\u9A9A\uFF0C\u975E\u5E38\u559C\u6B22\u53BB\u642D\u8BAA\u5973\u751F\uFF0C\u62CD\u5973\u751F\u4EEC\u7684cos\u7167\u7247\uFF0C\u6709\u65F6\u5019\u8138\u76AE\u751A\u81F3\u8D85\u7EA7\u539A\uFF0C\u4E5F\u56E0\u6B64\u5728\u5267\u56E2\u91CC\u5973\u751F\u5BF9\u4ED6\u7684\u8BC4\u4EF7\u90FD\u4E0D\u9AD8\u3002<br> \u672C\u4EBA\u62E5\u6709\u9AD8\u8D85\u7684\u6253\u620F\u6F14\u51FA\u80FD\u529B\uFF0C\u53EF\u4EE5\u5B8C\u7F8E\u5730\u505A\u5230\u4EE5\u5047\u4E71\u771F\uFF0C\u5E94\u8BE5\u8BF4\u4ED6\u7684\u5251\u672F\u662F\u8D27\u771F\u4EF7\u5B9E\u5730\u5F3A\uFF0C\u5728\u5165\u90E8\u6D4B\u8BD5\u4E2D\u66FE\u8F7B\u677E\u5E94\u5BF9\u5176\u4ED6\u90E8\u5458\u96BE\u4EE5\u5339\u654C\u768450\u4EBAboss\u7EA7npc\uFF0C\u5728\u88AB\u5DE6\u4E18\u6597\u8981\u6C42\u51B3\u6597\u7684\u65F6\u5019\u8D25\u5317\uFF0C\u4F46\u662F\u88AB\u963F\u6597\u53D1\u73B0\u662F\u6545\u610F\u653E\u6C34\uFF0C\u5BFC\u81F4\u5BF9\u65B9\u975E\u5E38\u607C\u706B\uFF0C\u5728\u5176\u540E\u7ECF\u5E38\u88AB\u963F\u6597\u63D0\u51FA\u5404\u79CD\u5404\u6837\u7684\u51B3\u6597\u8981\u6C42\u3002\u4E5F\u56E0\u6B64\uFF0C\u4E24\u4E2A\u4EBA\u751A\u81F3\u5728\u8868\u6F14\u7684\u8FC7\u7A0B\u4E2D\u5047\u620F\u771F\u505A\uFF0C\u6218\u6597\u6253\u5F97\u5F02\u5E38\u7CBE\u5F69\uFF0C\u4E00\u5EA6\u8BA9\u89C2\u4F17\u76EE\u77AA\u53E3\u5446\u3002<br> \u5BF9\u963F\u6597\u7684\u51B3\u6597\u8981\u6C42\u4E00\u822C\u4E0D\u4F1A\u62D2\u7EDD\uFF0C\u53CD\u800C\u4F1A\u8F83\u771F\uFF0C\u5728\u90A3\u4E00\u6B21\u8D25\u5317\u540E\u552F\u72EC\u9762\u5BF9\u4ED6\u7684\u65F6\u5019\u4E0D\u60F3\u8F93\u6389\uFF0C\u4E0B\u610F\u8BC6\u5730\u4E0E\u963F\u6597\u7684\u4EF7\u503C\u89C2\u4EA7\u751F\u5BF9\u7ACB\uFF0C\u5E76\u4E0D\u80FD\u5BB9\u5FCD\u81EA\u5DF1\u8F93\u7ED9\u8FD9\u6837\u7684\u5BB6\u4F19\u3002<br> \u5B9E\u9645\u4E0A\u4E3A\u539F\u4E16\u754C\u7684\u52C7\u8005\uFF0C\u62E5\u6709\u767E\u5206\u4E4B\u4E00\u767E\u80FD\u591F\u6253\u5012\u9B54\u738B\u7684\u5B9E\u529B\uFF0C\u4F46\u662F\u5728\u4E8E\u9B54\u738B\u6218\u6597\u4E4B\u524D\u88AB\u79D8\u5BC6\u201C\u6697\u6740\u201D\uFF0C\u901A\u8FC7\u7F18\u83BA\u4F20\u8BF4\u6765\u5230\u4E86\u539F\u8272\u4E16\u754C\u3002\u5C3D\u7BA1\u8D35\u4E3A\u52C7\u8005\uFF0C\u4E0D\u77E5\u4E3A\u4EC0\u4E48\u5374\u5E76\u4E0D\u559C\u6B22\u52C7\u8005\u8FD9\u4E2A\u8BCD\uFF0C\u7ECF\u5E38\u8C03\u4F83\u201C\u52C7\u8005\u4E0D\u8FC7\u5C31\u662F\u5E78\u8FD0\u4E00\u70B9\u88AB\u5723\u5251\u9009\u4E2D\u7684\u5BB6\u4F19\u800C\u5DF2\u201D\uFF0C\u4E5F\u4E0D\u592A\u559C\u6B22\u63A5\u82F1\u96C4\u5F0F\u7684\u89D2\u8272\u3002\u5176\u62E5\u6709\u7684\u5723\u5251\u4E5F\u8868\u73B0\u51FA\u4E00\u526F\u6BEB\u4E0D\u7231\u60DC\u7684\u6837\u5B50\uFF0C\u5728cos\u90E8\u7684\u9053\u5177\u6216\u8005\u821E\u53F0\u9700\u8981\u6746\u5B50\u652F\u6491\u7684\u65F6\u5019\uFF0C\u603B\u4F1A\u8BF4\u201C\u90A3\u5C31\u7528\u8FD9\u4E2A\u5BB6\u4F19\u5427\uFF0C\u653E\u5FC3\u7EDD\u5BF9\u4E0D\u4F1A\u538B\u574F\u7684\u201D\uFF0C\u4E5F\u56E0\u6B64\u5723\u5251\u541B\u603B\u662F\u60B2\u5267\u5730\u6210\u4E3A\u201C\u4E00\u822C\u9053\u5177\u201D\u3002<br> \u5728\u8FDB\u5165\u539F\u8272\u4E16\u754C\u540E\u63A5\u89E6\u5230\u4E86galgame\uFF0C\u4ECE\u6B64\u9677\u5165\u4E86\u8FBE\u6210\u5168\u89D2\u8272\u653B\u7565\u6210\u5C31\u7684\u5F3A\u8FEB\u75C7\uFF0C\u53D1\u81EA\u5185\u5FC3\u5730\u8BA4\u4E3A\u201C\u8FD9\u662F\u6BD4\u539F\u4E16\u754C\u7684\u6F58\u591A\u62C9\u7684\u5B9D\u85CF\u66F4\u52A0\u73CD\u8D35\u7684\u5723\u7269\u201D\u3002<br> \u672C\u4EBA\u5728\u539F\u4E16\u754C\u65E0\u8BBA\u662F\u9B54\u6CD5\u8FD8\u662F\u5251\u672F\u90FD\u5F88\u5F3A\u5927\uFF0C\u7531\u4E8E\u53D7\u5230\u539F\u8272\u4E16\u754C\u89C4\u5219\u9650\u5236\uFF0C\u529B\u91CF\u53EA\u80FD\u53D1\u6325\u5341\u4E00\u5206\u4E4B\u4E00\uFF0C\u53D7\u5230\u7F18\u83BA\u5A18\u795D\u798F\u540E\u53EF\u4EE5\u6A21\u62DF\u51FA\u63A5\u8FD1\u4E8C\u5206\u4E4B\u4E00\u7684\u6548\u679C\u3002<br> \u96FE\u591C\u8001\u5E08\u4F3C\u4E4E\u77E5\u9053\u4ED6\u7684\u8FC7\u53BB\uFF0C\u8BC4\u4EF7\u4ED6\u4E3A\u201C\u5C31\u662F\u56E0\u4E3A\u4ED6\u6709\u5982\u4F24\u75D5\u7D2F\u7D2F\u7684\u96C4\u9E70\uFF0C\u6325\u821E\u7684\u5251\u4E2D\u65E0\u4E00\u4E1D\u738B\u8005\u518D\u4E34\u7684\u6C14\u52BF\uFF0C\u53EA\u6709\u5B64\u72EC\u65E0\u52A9\u7684\u6C42\u6551\uFF0C\u6211\u624D\u8BA9\u4ED6\u62C5\u4EFB\u8FD9\u6B21\u7684\u6253\u620F\u7684\u3002\u201D</p><h3 id="\u963F\u6597" tabindex="-1"><a class="header-anchor" href="#\u963F\u6597" aria-hidden="true">#</a> \u963F\u6597</h3><p>\u672C\u540D\u4E3A\u5DE6\u4E18\u6597\uFF0C\u4E3A\u539F\u4E16\u754C\u7684\u4E1C\u65B9\u6700\u5F3A\u5251\u4FA0\u3002\u4E00\u8EAB\u4E1C\u65B9\u6B66\u4FA0\u98CE\u683C\u7684\u88C5\u626E\uFF0C\u80CC\u4E0A\u559C\u6B22\u88AB\u4E00\u628A\u9053\u5251\uFF0C\u4F46\u540E\u6765\u88AB\u96FE\u591C\u6362\u6210\u4E00\u6A21\u4E00\u6837\u7684\u9053\u5177\u5251\u3002<br> \u4F59\u5F26\u5267\u56E2\u7684\u6253\u620F\u62C5\u5F53\uFF0C\u4E0E\u963F\u52C7\u62E5\u6709\u4E0D\u76F8\u4E0A\u4E0B\u7684\u5F3A\u5927\u5B9E\u529B\u3002\u4E2A\u6027\u9AD8\u50B2\u81EA\u8D1F\uFF0C\u5BF9\u81EA\u5DF1\u7684\u5B9E\u529B\u76F8\u5F53\u6709\u81EA\u4FE1\u3002\u552F\u4E00\u6015\u7684\u4EBA\u662F\u96FE\u591C\u8001\u5E08\u3002<br> \u8FDB\u5165\u539F\u8272\u4E16\u754C\u540E\u542C\u95FB\u4F59\u5F26\u5267\u573A\u7684\u963F\u52C7\u5B9E\u529B\u5F88\u5F3A\uFF0C\u4E8E\u662F\u76F4\u63A5\u95EF\u5165\u539F\u533A\u5E76\u8981\u6C42\u8DDF\u4ED6\u51B3\u6597\uFF0C\u80FD\u548C\u963F\u52C7\u6253\u5F97\u96BE\u89E3\u96BE\u5206\uFF0C\u4F46\u6700\u540E\u4E00\u51FB\u4E2D\u5374\u88AB\u963F\u52C7\u610F\u5916\u653E\u6C34\u800C\u83B7\u80DC\uFF0C\u4E8B\u540E\u975E\u5E38\u4E0D\u6EE1\uFF0C\u8981\u6C42\u518D\u6218\u3002\u7136\u800C\u88AB\u96FE\u591C\u8D81\u673A\u6293\u4F4F\u673A\u4F1A\u5FFD\u60A0\u5165\u56E2\uFF0C\u4ECE\u800C\u6210\u4E3A\u4E86\u5267\u56E2\u91CC\u7684\u6F14\u5458\u517C\u6253\u6742\u7684\u3002<br> \u201C\u51B3\u80DC\u8D1F\u5427\uFF0C\u963F\u52C7\uFF01\u201D<br> \u5E73\u65E5\u91CC\u5E38\u5E38\u627E\u5404\u79CD\u673A\u4F1A\u8DDF\u963F\u52C7\u51B3\u6597\uFF0C\u6BD4\u5982\u8DD1\u6B65\u3001\u6E38\u6CF3\uFF0C\u751A\u81F3\u5728\u6700\u4E0D\u613F\u610F\u5E72\u7684\u5973\u88C5\u7684\u4E8B\u60C5\u4E0A\uFF08\u88AB\u96FE\u591C\u5A01\u80C1\uFF09\uFF0C\u4E5F\u4E0D\u4F1A\u8BA9\u6B65\uFF08\u5F53\u7136\uFF0C\u4E24\u4E2A\u808C\u8089\u7537\u7684\u5973\u88C5\u6CA1\u4EC0\u4E48\u597D\u770B\u7684\uFF09\u3002\u201C\u54FC\uFF0C\u4F60\u7684\u53EF\u7231\u7A0B\u5EA6\u5C31\u8FD9\u70B9\uFF0C\u8FDE\u5728\u4E0B\u7684\u7F8E\u817F\u90FD\u6BD4\u4E0D\u4E0A\u554A\uFF01\u201D\u3002<br> \u5728\u5973\u88C5\u8BF1\u60D1\u7684\u6BD4\u8D5B\u4E2D\uFF0C\u7531\u4E8E\u642D\u8BAA\u7684\u65F6\u5019\u5BF9\u7537\u751F\u53D1\u72C2\u4E00\u822C\u5730\u8BF4\u201C\u5E05\u54E5\uFF01\u4ECA\u665A\u8DDF\u5728\u4E0B\u7EA6\u4F1A\u600E\u4E48\u6837\u554A\uFF01\u6211\u4F1A\u5C06\u4E00\u4E9B\u6709\u8DA3\u7684\uFF0C\u5173\u4E8E\u8F7B\u677E\u8E42\u8E8F\u5343\u5E74\u8001\u5996\u7684\u79D8\u7C4D\u8BF4\u7ED9\u4F60\u542C\u7684\uFF0C\u554A\u54C8\u54C8\u54C8\u54C8\uFF01\uFF01\uFF01\uFF01\uFF01\uFF01\uFF01\uFF01\uFF01\u201D<br> \u5BFC\u81F4\u7ED3\u679C\u8DDF\u963F\u52C7\u4E00\u6837\u4E3A0\uFF0C\u6700\u540E\u4E24\u4E2A\u4EBA\u53EA\u80FD\u63A5\u53D7\u5E73\u5C40\u3002<br> \u4E0E\u963F\u52C7\u4E0D\u4E00\u6837\uFF0C\u672C\u4EBA\u5411\u5F80\u8FFD\u6C42\u66F4\u5F3A\u5927\u7684\u529B\u91CF\uFF0C\u5728\u4E0E\u963F\u52C7\u7684\u5BF9\u51B3\u548C\u6392\u7EC3\u7684\u8FC7\u7A0B\u4E2D\u611F\u53D7\u5230\u81EA\u5DF1\u7684\u63D0\u9AD8\uFF0C\u8BA4\u4E3A\u529B\u91CF\u80FD\u5B88\u62A4\u4E00\u5207\uFF0C\u4F46\u8FD9\u4E2A\u884C\u4E3A\u5374\u88AB\u963F\u52C7\u55E4\u4E4B\u4EE5\u9F3B\uFF0C\u56E0\u6B64\u5F88\u4E0D\u540C\u610F\u4ED6\u7684\u60F3\u6CD5\u3002<br> \u559C\u6B22\u70ED\u8840\u7C7B\u7684\u6F2B\u753B\uFF0C\u603B\u559C\u6B22\u628A\u81EA\u5DF1\u8DDF\u6F2B\u753B\u91CC\u7684\u4E3B\u89D2\u4F5C\u6BD4\u8F83\u3002<br> \u7531\u4E8E\u88AB\u4EBA\u53EB\u201C\u963F\u6597\u201D\u603B\u4F1A\u8BA9\u4ED6\u611F\u89C9\u81EA\u5DF1\u88AB\u6697\u6307\u662F\u539F\u4E16\u754C\u4E2D\u7684\u67D0\u4E2A\u5E9F\u67F4\uFF0C\u6240\u4EE5\u975E\u5E38\u4E0D\u559C\u6B22\u8FD9\u4E2A\u79F0\u547C\uFF0C\u559C\u6B22\u88AB\u865A\u9171\u53EB\u505A\u201C\u5DE6\u4E18\u5927\u4FA0\u201D\u3002</p><h3 id="\u865A\u9171" tabindex="-1"><a class="header-anchor" href="#\u865A\u9171" aria-hidden="true">#</a> \u865A\u9171</h3><p>\u4F59\u5F26\u5267\u56E2\u91CC\u7684\u6587\u620F\u62C5\u5F53\uFF0C\u672C\u4E3A\u539F\u4E16\u754C\u7684\u9B54\u6CD5\u5C11\u5973\uFF0C\u540E\u6765\u56E0\u4E3A\u53D1\u73B0\u4E86\u7F18\u83BA\u9B54\u6CD5\u7684\u5492\u8BED\u800C\u8FDB\u5165\u539F\u8272\u4E16\u754C\u3002<br> \u62E5\u6709\u53EF\u7231\u7684\u9B54\u6CD5\u5C11\u5973\u7684\u88C5\u626E\uFF0C\u4F46\u5374\u7ECF\u5E38\u88AB\u8BA4\u4E3A\u662Fcosplay\uFF0C\u88AB\u95EE\u662F\u51FA\u81EA\u54EA\u90E8\u756A\u7684\u89D2\u8272\u7684\u65F6\u5019\u603B\u662F\u89E3\u91CA\u4E0D\u6E05\u3002\u597D\u51E0\u6B21\u60F3\u5229\u7528\u9B54\u6CD5\u8BC1\u660E\u81EA\u5DF1\u7684\u8EAB\u4EFD\uFF0C\u5374\u603B\u56E0\u4E3A\u4E00\u4E9B\u539F\u56E0\u6CA1\u6709\u88AB\u4EBA\u770B\u89C1\u6216\u8005\u88AB\u8BA4\u4E3A\u662F\u7279\u6548\u3002<br> \u7531\u4E8E\u5F3A\u5927\u7684\u5634\u70AE\u80FD\u529B\uFF0C\u540E\u6765\u88AB\u96FE\u591C\u7269\u8272\u4E3A\u6587\u620F\u62C5\u5F53\uFF0C\u4F46\u662F\u62C5\u4EFB\u6253\u620F\u7684\u65F6\u5019\u7ECF\u5E38\u662F\u4E00\u5F00\u573A\u5C31\u4FBF\u5F53\u7684\u89D2\u8272\u3002<br> \u4E2A\u6027\u6709\u70B9\u5929\u7136\uFF0C\u5374\u53C8\u5BCC\u6709\u6B63\u4E49\u611F\uFF0C\u6E29\u67D4\u7684\u5979\u548C\u7F18\u83BA\u5A18\u975E\u5E38\u5408\u5F97\u6765\uFF0C\u4E0D\u8FC7\u8FD9\u88AB\u96FE\u591C\u89E3\u91CA\u4E3A\u201C\u6027\u683C\u662F\u4E00\u65B9\u9762\uFF0C\u4E24\u4E2A\u4EBA\u5F88\u5408\u5927\u6982\u662F\u5973\u795E\u7684\u76F8\u6027\u5427\u3002\u201D\u88AB\u8BC1\u5B9E\u5728\u539F\u4E16\u754C\u62E5\u6709\u795E\u4E00\u822C\u80FD\u6BC1\u706D\u4E16\u754C\u7684\u80FD\u529B\uFF0C\u7531\u4E8E\u5B88\u62A4\u8FC7\u7684\u4EBA\u5BB3\u6015\u5979\u7684\u529B\u91CF\u7EC8\u6709\u4E00\u5929\u62DB\u6765\u4E0D\u5E78\uFF0C\u88AB\u9063\u9001\u5230\u539F\u8272\u4E16\u754C\u3002\u7531\u4E8E\u88AB\u539F\u8272\u4E16\u754C\u7684\u89C4\u5219\u9650\u5236\uFF0C\u7406\u8BBA\u4E0A\u8FD9\u4EFD\u529B\u91CF\u4E0D\u4F1A\u89C9\u9192\uFF0C\u4F46\u662F\u53EA\u662F\u52C9\u5F3A\u6291\u5236\u3002<br> \u5BF9\u7ECF\u5E38\u5435\u67B6\u7684\u963F\u52C7\u548C\u963F\u6597\u663E\u5F97\u4E0D\u77E5\u6240\u63AA\uFF0C\u89C9\u5F97\u81EA\u5DF1\u6CA1\u80FD\u529B\u5236\u6B62\u4ED6\u4EEC\u6253\u67B6\uFF0C\u5BF9\u5267\u56E2\u7684\u6210\u5458\u975E\u5E38\u5173\u5FC3\uFF0C\u5176\u7B11\u5BB9\u751A\u81F3\u8FDE\u96FE\u591C\u4E5F\u89C9\u5F97\u6CBB\u6108\u3002<br> \u867D\u8BF4\u5982\u6B64\uFF0C\u5F53\u88AB\u6478\u5934\u7684\u65F6\u5019\u4F1A\u7A81\u7136\u9677\u5165\u66B4\u8D70\uFF0C\u75AF\u72C2\u5230\u8FDE\u963F\u52C7\u4ED6\u4EEC\u4E5F\u611F\u5230\u5BB3\u6015\u3002<br> \u559C\u6B22\u5B50\u4F9B\u5411\u7684\u9B54\u6CD5\u5C11\u5973\u9898\u6750\u7684\u52A8\u753B\uFF0C\u5BF9\u52A8\u753B\u91CC\u7684\u9B54\u6CD5\u5C11\u5973\u4EEC\u6709\u7740\u83AB\u540D\u7684\u7FA1\u6155\u3002</p><h3 id="\u65E0\u97F3" tabindex="-1"><a class="header-anchor" href="#\u65E0\u97F3" aria-hidden="true">#</a> \u65E0\u97F3</h3><p>\u4F59\u5F26\u5267\u56E2\u91CC\u7684\u9053\u5177\u5E08\uFF0C\u81EA\u79F0\u81EA\u5DF1\u662F\u65E0\u6240\u4E0D\u80FD\u7684\u5929\u624D\u5C11\u5973\u3002<br> \u5934\u53D1\u624E\u6210\u51B2\u5929\u8FAB\uFF0C\u989D\u5934\u6234\u7740\u62A4\u76EE\u955C\uFF0C\u5916\u52A0\u5E73\u65F6\u7A7F\u7684\u5DE5\u88C5\u88E4\uFF0C\u770B\u4E0A\u53BB\u50CF\u662F\u5DE5\u4F5C\u574A\u91CC\u7684\u94C1\u5320\u3002<br> \u8D1F\u8D23\u4E3A\u4F59\u5F26\u5267\u56E2\u6253\u9020\u8868\u6F14\u9700\u8981\u7684\u9053\u5177\u8FD8\u6709\u670D\u88C5\uFF0C\u867D\u7136\u53EA\u662F\u7528\u4E8E\u6F14\u620F\uFF0C\u4F46\u662F\u672C\u4EBA\u5236\u9020\u7684\u7ECF\u5E38\u662F\u767E\u5206\u767E\u7684\u771F\u8D27\uFF0C\u65E0\u8BBA\u662F\u5200\u5251\u8FD8\u662F\u67AA\u68B0\uFF0C\u90FD\u53EF\u4EE5\u8BF4\u5F97\u4E0A\u662F\u540D\u4E3A\u201C\u6B66\u5668\u201D\u7684\u73A9\u610F\u513F\uFF0C\u4E0D\u8FC7\u672C\u4EBA\u5728\u8BBE\u8BA1\u7684\u65F6\u5019\u4E5F\u4F1A\u52A0\u4E0A\u4FDD\u62A4\u63AA\u65BD\u9632\u6B62\u53D1\u751F\u610F\u5916\u3002\u6709\u65F6\u5019\u5979\u751A\u81F3\u53EF\u4EE5\u505A\u51FA\u8D85\u65F6\u4EE3\u7684\u9053\u5177\uFF0C\u4F8B\u5982\u53EA\u5B58\u5728\u4E8E\u756A\u5267\u6216\u8005\u4F5C\u54C1\u91CC\u7684\u98DE\u8239\u6216\u8005\u9B54\u6CD5\u5B9D\u5177\uFF0C\u90FD\u80FD\u5B8C\u7F8E\u8FD8\u539F\u3002<br> \u5E73\u65F6\u7ECF\u5E38\u81EA\u5938\uFF0C\u6027\u683C\u4E0A\u76F8\u5F53\u5F00\u653E\uFF0C\u5C5E\u4E8E\u770B\u8D77\u6765\u8BDD\u597D\u50CF\u4E0D\u591A\u4F46\u5B9E\u9645\u4E0A\u5F88\u95F7\u9A9A\u7684\u7C7B\u578B\u3002\u4E0D\u8FC7\u5DE5\u4F5C\u7684\u65F6\u5019\u76F8\u5F53\u8BA4\u771F\uFF0C\u5BF9\u4E8E\u9053\u5177\u7684\u6BCF\u4E00\u4E2A\u7EC6\u8282\u90FD\u6709\u7740\u4E25\u8C28\u7684\u8003\u636E\u3002<br> \u867D\u7136\u5916\u8868\u4E0A\u7ED9\u4EBA\u4E00\u79CD\u666E\u901A\u7684\u611F\u89C9\uFF0C\u4F46\u662F\u5F53cos\u8D77\u6765\u7684\u65F6\u5019\u7ECF\u5E38\u60CA\u8273\u4F17\u4EBA\uFF0C\u4ECE\u7F8E\u5C11\u5973\u7684\u7F8E\u5C11\u5973\u7684\u89D2\u8272\u90FD\u80FD\u80DC\u4EFB\uFF0C\u5C24\u5176\u662F\u7F8E\u5C11\u5E74\u8FD8\u6709\u4E00\u79CD\u72EC\u6709\u7684\u64A9\u4EBA\u6C14\u606F\uFF0C\u5176\u6740\u4F24\u529B\u751A\u81F3\u4F1A\u76D6\u8FC7\u96FE\u591C\u3002</p>',13),b=[d];function i(n,t){return a(),e("div",null,b)}const p=r(h,[["render",i],["__file","index.html.vue"]]);export{p as default};
